create database TriggerDemoDB;use TriggerDemoDB;CREATE TABLE Employees(    EmpId INT PRIMARY KEY,       -- Unique Employee ID    EmpName VARCHAR(50),         -- Employee Name    Salary INT                   -- Employee Salary);-- Inserting initial employee dataINSERT INTO Employees (EmpId, EmpName, Salary)VALUES(1, 'Mounika', 50000),(2, 'rama', 45000);CREATE TABLE EmployeeSalaryAudit --this t(--write only table--no application code will insert into this table directly--this table will record all entries related oto emp salayr    AuditId INT IDENTITY(1,1) PRIMARY KEY,    EmpId INT,    OldSalary INT,    NewSalary INT,    ChangedOn DATETIME DEFAULT GETDATE());select *from EmployeeSalaryAudit;--creating after updare triggerCREATE TRIGGER trg_AuditEmployeeSalaryON EmployeesAFTER UPDATEASBEGIN    -- Prevent unnecessary execution    IF NOT UPDATE(Salary)        RETURN;    INSERT INTO EmployeeSalaryAudit (EmpId, OldSalary, NewSalary)    SELECT         d.EmpId,        d.Salary AS OldSalary,        i.Salary AS NewSalary    FROM deleted d    INNER JOIN inserted i        ON d.EmpId = i.EmpId;END;--performing update operation that call trigger indirectlt with data amd performs entry into audit table--calling triggerupdate Employees set Salary=Salary+12000where EmpId=1;-- Verify updated salarySELECT * FROM Employees;-- Verify audit entry created by triggerSELECT * FROM EmployeeSalaryAudit;